from typing import List
import heapq
from collections import defaultdict

class DualHeap:
    def __init__(self, k: int):
        self.small = []  # max-heap (store negatives)
        self.large = []  # min-heap
        self.delayed = defaultdict(int)
        self.k = k
        self.small_size = 0
        self.large_size = 0

    def prune_small(self):
        while self.small and self.delayed[-self.small[0]] > 0:
            num = -heapq.heappop(self.small)
            self.delayed[num] -= 1

    def prune_large(self):
        while self.large and self.delayed[self.large[0]] > 0:
            num = heapq.heappop(self.large)
            self.delayed[num] -= 1

    def make_balance(self):
        if self.small_size > self.large_size + 1:
            num = -heapq.heappop(self.small)
            self.small_size -= 1
            heapq.heappush(self.large, num)
            self.large_size += 1
            self.prune_small()
        elif self.small_size < self.large_size:
            num = heapq.heappop(self.large)
            self.large_size -= 1
            heapq.heappush(self.small, -num)
            self.small_size += 1
            self.prune_large()

    def insert(self, num: int):
        if not self.small or num <= -self.small[0]:
            heapq.heappush(self.small, -num)
            self.small_size += 1
        else:
            heapq.heappush(self.large, num)
            self.large_size += 1
        self.make_balance()

    def erase(self, num: int):
        self.delayed[num] += 1
        if num <= -self.small[0]:
            self.small_size -= 1
            if num == -self.small[0]:
                self.prune_small()
        else:
            self.large_size -= 1
            if self.large and num == self.large[0]:
                self.prune_large()
        self.make_balance()

    def get_median(self) -> float:
        if self.k % 2 == 1:
            return float(-self.small[0])
        else:
            return (-self.small[0] + self.large[0]) / 2.0

class Solution:
    def medianSlidingWindow(self, nums: List[int], k: int) -> List[float]:
        n = len(nums)
        dh = DualHeap(k)
        for i in range(k):
            dh.insert(nums[i])
        result = [dh.get_median()]
        for i in range(k, n):
            dh.insert(nums[i])
            dh.erase(nums[i - k])
            result.append(dh.get_median())
        return result

