from typing import List

class Solution:
    def smallestSufficientTeam(self, req_skills: List[str], people: List[List[str]]) -> List[int]:
        skill_index = {skill: i for i, skill in enumerate(req_skills)}
        m = len(req_skills)
        full_mask = (1 << m) - 1

        people_masks = []
        for skills in people:
            mask = 0
            for s in skills:
                mask |= 1 << skill_index[s]
            people_masks.append(mask)

        dp = {0: []}

        for i, pmask in enumerate(people_masks):
            if pmask == 0:
                continue
            current_dp_items = list(dp.items())
            for mask, team in current_dp_items:
                new_mask = mask | pmask
                if new_mask == mask:
                    continue
                if new_mask not in dp or len(team) + 1 < len(dp[new_mask]):
                    dp[new_mask] = team + [i]

        return dp[full_mask]

