from typing import List

class Solution:
    def tallestBillboard(self, rods: List[int]) -> int:
        dp = {0: 0}  # diff -> tallest height with this diff
        for x in rods:
            cur = dp.copy()
            for d, h in cur.items():
                # Option 1: add x to taller side
                nd = d + x
                dp[nd] = max(dp.get(nd, 0), h + x)

                # Option 2: add x to shorter side
                nd2 = abs(d - x)
                gain = x - d if x > d else 0
                dp[nd2] = max(dp.get(nd2, 0), h + gain)
        return dp.get(0, 0)

