from typing import List

class Solution:
    def wordBreak(self, s: str, wordDict: List[str]) -> List[str]:
        word_set = set(wordDict)
        max_len = max(len(w) for w in word_set) if word_set else 0
        memo = {}

        def dfs(start):
            if start == len(s):
                return [""]

            if start in memo:
                return memo[start]

            sentences = []
            for end in range(start + 1, min(len(s), start + max_len) + 1):
                word = s[start:end]
                if word in word_set:
                    rest_sentences = dfs(end)
                    for rest in rest_sentences:
                        if rest:
                            sentences.append(word + " " + rest)
                        else:
                            sentences.append(word)

            memo[start] = sentences
            return sentences

        return dfs(0)

